                        int ny = y + ddy[i];
                        if (nx >= 0 && ny < n && ny >= 0 && nx < n
                                && map[ny][nx] == '#') {
                            c++;
                        }
                    }
                    if (c > 1)
                        break;
                    map[y][x] = '#';
                    int nx = x + dx;
                    int ny = y + dy;
                    if (ny < top) {
                        dy = 0;
                        dx = 1;
                        left += 2;
                        nx = x + dx;
                        ny = y + dy;
                    } else if (nx > right) {
                        dy = 1;
                        dx = 0;
                        top += 2;
                        nx = x + dx;
                        ny = y + dy;
                    } else if (ny > bottom) {
                        dx = -1;
                        dy = 0;
                        right -= 2;
                    } else if (nx < left) {
                        dx = 0;
                        dy = -1;
                        bottom -= 2;
                    }
                    x += dx;
                    y += dy;
                }
                for (int i = 0; i < n; i++) {
                    for (int j = 0; j < n; j++) {
                        System.out.print(map[i][j]);
                    }
                    System.out.println();
                }
            }
            d--;
            if(d!=0)System.out.println();
        }
    }
}

