		while(true){
			int xx=x-vx[f], yy=y-vy[f];
			boolean flag=true;
			for(int i=0; i<4; i++){
				if(x+vx[i]==xx || y+vy[i]==yy)	continue;
				if(0>=x+vx[i]||x+vx[i]>n || 0>=y+vy[i]||y+vy[i]>n){
					flag=false;
					break;
				}
				if(c[x+vx[i]][y+vy[i]]=='#')	{
					flag=false;
					break;
				}
			}
			if(!flag){
				out.println("A:"+x+" "+y);
				break;
			}
			if(c[x+vx[f]][y+vy[f]]==' '){
				c[x][y]='#';
				//return solve141(x+vx[f], y+vy[f], n,c,vx,vy,f);
				x+=vx[f];	y+=vy[f];
				continue;
			}else{
				x=xx;	y=yy;
				f= f+1<4? f+1: 0;
				if(c[x+vx[f]][y+vy[f]]==' '){
					//return solve141(x, y, n,c,vx,vy,f);
					x+=vx[f];	y+=vy[f];
					continue;
				}
			}
