	boolean m2(int i, int j){
		if(i < 0 || i >= n || j < 0 || j >= n){
			return false;
		}	
		return !map[i][j];
	}
	boolean m3(int i, int j){
		if(i < 0 || i >= n || j < 0 || j >= n){
			return false;
		}	
		return map[i][j];
	}
	boolean check(int i, int j){
		if(!m2(i,j)){
			return false;
		}
		int[] di = {-1,0,1,0};
		int[] dj = {0,1,0,-1};
		int sum = 0;
		for(int z = 0; z< 4; z++){
			sum += m3(i+di[z],j+dj[z])?1:0;
		}
		return sum < 2;
