import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

public class Main {

	static final int UP = 1;
	static final int RIGHT = 2;
	static final int DOWN = 4;
	static final int LEFT = 8;

	public static void main(String[] args) throws IOException{
		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
		int numInput = Integer.parseInt(br.readLine());
		int width = 0;
		for(int i = 0; i < numInput; i++){
			width = Integer.parseInt(br.readLine());
			printSpiral(width);
			if(i != numInput - 1)System.out.println();
		}
	}

	public static void printSpiral(int n){
		if(n == 1){
			System.out.println("#");
			return;
		}
		boolean[][] spiral = createSpiral(n);
		for(int i = 0; i < spiral.length; i++){
			for(boolean row : spiral[i]){
				if(row == true)		System.out.print("#");
				else				System.out.print(" ");
			}
			System.out.println();
		}
	}

	private static boolean[][] createSpiral(int n){
		boolean[][] spiral = new boolean[n][n];
		//一番左
		for(int i = 0; i < n; i++){
			spiral[i][0] = true;
		}
		//それ以降
		int[] length = new int[2];
		length[0] = n - 1;
		length[1] = n - 1;
		int direction = RIGHT;
		int c = 0;
		int x = 0, y = 0;
		while(true){
			for(int i = 0; i < length[c % 2]; i++){
				switch(direction){
				case UP:	y--;	break;
				case RIGHT:	x++;	break;
				case DOWN:	y++;	break;
				case LEFT:	x--;	break;
				}
				spiral[y][x] = true;
			}
			length[c++ % 2] -= 2;
			if(length[0] + length[1] < 2)	break;
			direction = direction * 2 % 15;
		}
		return spiral;
	}
}

import java.util.*;
import java.io.*;

public class Main {

	public void change(int[] d){
		if( d[0] == -1 ){
			d[0] = 0;
			d[1] = 1;
		} else if( d[0] == 0 ){
			if( d[1] == 1 ){
				d[0] = 1;
				d[1] = 0;
			} else {
				d[0] = -1;
				d[1] = 0;
			}
		} else {
			d[0] = 0;
			d[1] = -1;
		}
	}
	public void solve() throws IOException{
		int n = nextInt();
		for(int i = 0; i < n; i++){
			int length = nextInt();
			if( length == 1 ){
				writer.println('#');
				continue;
			}
			char[][] ans = new char[length+2][length+2];
			for(int j = 0; j < ans.length; j++){
				for(int k = 0; k < ans.length; k++){
					ans[j][k] = ' ';
				}
			}
			int[] point = new int[2];
			point[0] = length; point[1] = 1;
			int[] dir = new int[2];
			dir[0] = -1; dir[1] = 0;
			int count = 1;
			int repeat = 30;
			while(true){
				if( point[0]+dir[0] > length || point[0]+dir[0] < 1 || point[1]+dir[1] > length || point[1]+dir[1] < 1 ){
					change(dir);
					count = 0;
					if( ans[point[0]+2*dir[0]][point[1]+2*dir[1]] == '#' ){
						ans[point[0]][point[1]] = '#';
						break;
					}
					continue;
				}
				int x = point[0]+dir[0];
				int y = point[1]+dir[1];
				if( ans[x][y] == '#' ){
					if( count < 3 ){
						break;
					}
					point[0] -= dir[0];
					point[1] -= dir[1];
					count = 0;
					change(dir);
					if( ans[point[0]+2*dir[0]][point[1]+2*dir[1]] == '#' ){
						break;
					}
				}
				ans[point[0]][point[1]] = '#';
				point[0] += dir[0];
				point[1] += dir[1];
				count++;
			}
			for(int j = 1; j <= length; j++){
				for(int k = 1; k <= length; k++){
					writer.print(ans[j][k]);
				}
				writer.println();
			}			
		}
		writer.flush();
	}			
	public static void main (String args[]) throws IOException{
		new Main().run();
	}

	BufferedReader reader;
	StringTokenizer tokenizer;
	PrintWriter writer;

	public void run() throws IOException{
		try{
			reader = new BufferedReader(new InputStreamReader(System.in));
			tokenizer = null;
			writer = new PrintWriter(System.out);
			solve();
			reader.close();
			writer.close();
		} catch (Exception e){
			e.printStackTrace();
			System.exit(1);
		}
	}
	public int nextInt() throws IOException{
		return Integer.parseInt(nextToken());
	}		
	public String nextToken() throws IOException{
		while( tokenizer == null || !tokenizer.hasMoreTokens() ){
			tokenizer = new StringTokenizer(reader.readLine());
		}
		return tokenizer.nextToken();
	}
}

package number_0137_0141;
import java.util.Scanner;

public class Main {
	static Scanner sc = new Scanner(System.in);

	//0141
	static class SpiralPattern {
		//左から順に上移動(0)、右移動(1)、下移動(2)、左移動(3)
		// ()内の数字はsetDirectionNum
		int[][] xy_dir = {{0, -1}, {1, 0}, {0, 1}, {-1, 0}};
		int y, x; //現在位置を記録する

		void print_a_spiral() {
			int inputNum = sc.nextInt();
			int[] spiralLen = new int[inputNum];
			for(int i=0; i<inputNum; i++) {
				spiralLen[i] = sc.nextInt();
			}
			for(int i=0; i<inputNum; i++) {
				char[][] field = new char[spiralLen[i]][spiralLen[i]];
				for(int j=0; j<spiralLen[i]; j++) {		// 拡張for文だと半角スペースが無視される？
					for(int k=0; k<spiralLen[i]; k++) {
						field[j][k] = ' ';
					}
				}

				y = spiralLen[i]-1;
				x = 0;
				field[y][x] = '#';
				int setNum_dir = 0; //初期値は上移動
				int dirChangeNum = 0; // 方向転換した回数
				for(;;) {
					if(dirChangeNum >= spiralLen[i] || spiralLen[i] == 1){
						break;
					}else if(y + xy_dir[setNum_dir][1]*2 < 0 || y + xy_dir[setNum_dir][1]*2 > spiralLen[i]-1 ||
							 x + xy_dir[setNum_dir][0]*2 < 0 || x + xy_dir[setNum_dir][0]*2 > spiralLen[i]-1) {
						y += xy_dir[setNum_dir][1];
						x += xy_dir[setNum_dir][0];
						field[y][x] = '#';
						dirChangeNum++;
						setNum_dir = dirChangeNum % 4;
					}else if(field[y + xy_dir[setNum_dir][1] * 2]
								  [x + xy_dir[setNum_dir][0] * 2] == '#') {
						dirChangeNum++;
						setNum_dir = dirChangeNum % 4;
					}else {
						y += xy_dir[setNum_dir][1];
						x += xy_dir[setNum_dir][0];
						field[y][x] = '#';
					}
				}

				for(int j=0; j<spiralLen[i]; j++) {
					for(int k=0; k<spiralLen[i]; k++) {
						System.out.print(field[j][k]);
					}
					System.out.println("");
				}
				if(i != spiralLen[i]-1) System.out.println("");
			}
		}
	}

	public static void main(String[] args) {
		SpiralPattern sp = new SpiralPattern();
		sp.print_a_spiral();
	}
}

import java.util.Scanner;


public class Main {

	char[][] makeSpiral(int n){
		char[][] pattern=new char[n][n];
		int dir=0;
		int x=0,y=n-n%2;
		int[] dx={0,1,0,-1};
		int[] dy={-1,0,1,0};
		for(int i=0;i<n;++i){
			for(int j=0;j<n;++j){
				pattern[i][j]=' ';
			}
		}
		int notTurn=2;
		boolean end=false;
		while(!end){

			if(n%2==0||notTurn<2){
				x+=dx[dir];
				y+=dy[dir];
			}
			--notTurn;

			switch(dir){
			case 0:
				if(y<=0||(y!=1&&pattern[x][y-2]=='*')){
					if(notTurn>0){
						end=true;
					}
					else{
						dir=1;
						notTurn=2;
					}
				}
				break;
			case 1:
				if(x>=n-1||(x!=n-2&&pattern[x+2][y]=='*')){
					if(notTurn>0){
						end=true;
					}
					else{
						dir=2;
						notTurn=2;
					}
				}
				break;
			case 2:
				if(y>=n-1||(y!=n-2&&pattern[x][y+2]=='*')){
					if(notTurn>0){
						end=true;
					}
					else{
						dir=3;
						notTurn=2;
					}
				}
				break;
			case 3:
				if(x<=0||(x!=1&&pattern[x-2][y]=='*')){
					if(notTurn>0){
						end=true;
					}
					else{
						dir=0;
						notTurn=2;
					}
				}
				break;
			}
			pattern[x][y]='*';
		}
		return pattern;
	}

	void io(){
		Scanner s=new Scanner(System.in);
		int setnum=s.nextInt();
		for(int i=0;i<setnum;++i){
			int n=s.nextInt();
			char[][] pattern=makeSpiral(n);

			if(i>0)System.out.println();
			for(int y=0;y<n;++y){
				for(int x=0;x<n;++x){
					System.out.print(pattern[x][y]);
				}
				System.out.println();
			}
		}
		s.close();
	}

	public static void main(String[] args) {
		new Main().io();
	}

}

