					field[i][n - 1] = '#';
				}
				debug2(field);
			} else {
				for (int i = 0; i < n; i++) {
					field[i][n - 1] = '#';
				}
				int nowX = n - 1;
				int nowY = n - 1;
				boolean go = true;
