					go = false;
					while (nowX - 2 >= 0 && field[nowY - 1][nowX - 1] != '#'
							&& field[nowY][nowX - 2] != '#'
							&& field[nowY][nowX - 1] != '#') {
						field[nowY][--nowX] = '#';
						go = true;
					}
					while (nowY - 2 >= 0 && field[nowY - 1][nowX + 1] != '#'
							&& field[nowY - 2][nowX] != '#'
							&& field[nowY - 1][nowX] != '#') {
						field[--nowY][nowX] = '#';
						go = true;
					}
					while (nowX + 2 < n && field[nowY + 1][nowX + 1] != '#'
							&& field[nowY][nowX + 2] != '#'
							&& field[nowY][nowX + 1] != '#') {
						field[nowY][++nowX] = '#';
						go = true;
					}
					while (nowY + 2 < n && field[nowY + 1][nowX - 1] != '#'
							&& field[nowY + 2][nowX] != '#'
							&& field[nowY + 1][nowX] != '#') {
						field[++nowY][nowX] = '#';
						go = true;
					}
				}
				debug2(field);
