			System.out.println(new String(array[i]));
		}
	}

	class MyScanner {
		int nextInt() {
			try {
				int c = System.in.read();
				while (c != '-' && (c < '0' || '9' < c))
					c = System.in.read();
				if (c == '-')
					return -nextInt();
				int res = 0;
				do {
					res *= 10;
					res += c - '0';
					c = System.in.read();
				} while ('0' <= c && c <= '9');
				return res;
			} catch (Exception e) {
				return -1;
			}
		}

		double nextDouble() {
			return Double.parseDouble(next());
		}

		String next() {
			try {
				StringBuilder res = new StringBuilder("");
				int c = System.in.read();
				while (Character.isWhitespace(c))
					c = System.in.read();
				do {
					res.append((char) c);
				} while (!Character.isWhitespace(c = System.in.read()));
				return res.toString();
			} catch (Exception e) {
				return null;
			}
		}
	}
}

import java.util.Arrays;
import java.util.Scanner;

public class Main {

    public static void main(String[] args) throws Exception {
        Scanner sc = new Scanner(System.in);
        int d = sc.nextInt();
        while (true) {
            if (d == 0)
                break;
            int n = sc.nextInt();
            if (n == 1) {
                System.out.println("#");
            } else {
                char[][] map = new char[n][n];
                for (int i = 0; i < n; i++) {
                    Arrays.fill(map[i], ' ');
                }
                int x = 0;
                int y = n - 1;
                int dx = 0;
                int dy = -1;
                int right = n - 1;
                int left = 0;
                int top = 0;
                int bottom = n - 1;
                while (true) {
                    if (map[y][x] == '#')
                        break;
                    int[] ddx = { 0, 1, 0, -1 };
                    int[] ddy = { 1, 0, -1, 0 };
                    int c = 0;
                    for (int i = 0; i < 4; i++) {
                        int nx = x + ddx[i];
                        int ny = y + ddy[i];
                        if (nx >= 0 && ny < n && ny >= 0 && nx < n
                                && map[ny][nx] == '#') {
                            c++;
                        }
                    }
                    if (c > 1)
                        break;
                    map[y][x] = '#';
                    int nx = x + dx;
                    int ny = y + dy;
                    if (ny < top) {
                        dy = 0;
                        dx = 1;
                        left += 2;
                        nx = x + dx;
                        ny = y + dy;
                    } else if (nx > right) {
                        dy = 1;
                        dx = 0;
                        top += 2;
                        nx = x + dx;
                        ny = y + dy;
                    } else if (ny > bottom) {
                        dx = -1;
                        dy = 0;
                        right -= 2;
                    } else if (nx < left) {
                        dx = 0;
                        dy = -1;
                        bottom -= 2;
                    }
                    x += dx;
                    y += dy;
                }
                for (int i = 0; i < n; i++) {
                    for (int j = 0; j < n; j++) {
                        System.out.print(map[i][j]);
                    }
                    System.out.println();
                }
            }
            d--;
        }
    }
}

import java.util.Scanner;

public class Main {
	static Scanner sc = new Scanner(System.in);

	//0141
	static class SpiralPattern {
		//左から順に上移動(0)、右移動(1)、下移動(2)、左移動(3)
		// ()内の数字はsetDirectionNum
		int[][] xy_dir = {{0, -1}, {1, 0}, {0, 1}, {-1, 0}};
		int y, x; //現在位置を記録する

		void print_a_spiral() {
			int inputNum = sc.nextInt();
			int[] spiralLen = new int[inputNum];
			for(int i=0; i<inputNum; i++) {
				spiralLen[i] = sc.nextInt();
			}
			for(int i=0; i<inputNum; i++) {
				char[][] field = new char[spiralLen[i]][spiralLen[i]];
				for(int j=0; j<spiralLen[i]; j++) {		// 拡張for文だと半角スペースが無視される？
					for(int k=0; k<spiralLen[i]; k++) {
						field[j][k] = ' ';
					}
				}

				y = spiralLen[i]-1;
				x = 0;
				field[y][x] = '#';
				int setNum_dir = 0; //初期値は上移動
				int dirChangeNum = 0; // 方向転換した回数
				for(;;) {
					if(dirChangeNum >= spiralLen[i] || spiralLen[i] == 1){
						break;
					}else if(y + xy_dir[setNum_dir][1]*2 < 0 || y + xy_dir[setNum_dir][1]*2 > spiralLen[i]-1 ||
							 x + xy_dir[setNum_dir][0]*2 < 0 || x + xy_dir[setNum_dir][0]*2 > spiralLen[i]-1) {
						y += xy_dir[setNum_dir][1];
						x += xy_dir[setNum_dir][0];
						field[y][x] = '#';
						dirChangeNum++;
						setNum_dir = dirChangeNum % 4;
					}else if(field[y + xy_dir[setNum_dir][1] * 2]
								  [x + xy_dir[setNum_dir][0] * 2] == '#') {
						dirChangeNum++;
						setNum_dir = dirChangeNum % 4;
					}else {
						y += xy_dir[setNum_dir][1];
						x += xy_dir[setNum_dir][0];
						field[y][x] = '#';
					}
				}

				for(int j=0; j<spiralLen[i]; j++) {
					for(int k=0; k<spiralLen[i]; k++) {
						System.out.print(field[j][k]);
					}
					System.out.println("");
				}
				System.out.println("");
			}
		}
	}

	public static void main(String[] args) {
		SpiralPattern sp = new SpiralPattern();
		sp.print_a_spiral();
	}
}

import java.io.BufferedReader;
