import java.io.*;
import java.util.*;

public class Main {
    int cx, cy;
    Scanner sc;

    Main() {
        sc = new Scanner(System.in);
        dataset = sc.nextInt();
    }

    int dataset;
    int n;
    char dir;
    char[][] map;
    void init() {
        n = sc.nextInt();
        map = new char[n][n];
        for (int i = 0; i < n; i++) 
            for (int j = 0; j < n; j++)
                map[i][j] = ' ';
    }

    boolean first_output = true;

    void run() {
        for (int d = 0; d < dataset; d++) {
            init();
            for (int i = n-1; i >= 0 ; i--)  map[i][0] = '#';
            for (int i = 0; i < n ; i++)  map[0][i] = '#';
            for (int i = 0; i < n ; i++)  map[i][n-1] = '#';
            for (int i = n-1; i >= 2 ; i--)  map[n-1][i] = '#';

            if (n == 2) map[1][1] = ' ';

            if (n <= 4) {
                if (!first_output) 
                    System.out.println("");
                printmap();
                first_output = false;
                continue;
            }

            dir = 'U';
            cx = 2;
            cy = n-1;

fill:
            while (true) {
                switch (dir) {
                    case 'L':
                        if (map[cy][cx-2] == '#') break fill;
                        while (map[cy][cx-2] != '#') {
                            cx--;
                            map[cy][cx] = '#';
                        }
                        if (map[cy][cx+2] != '#') break fill;
                        dir = 'U';
                        break;
                    case 'U':
                        if (map[cy-2][cx] == '#') break fill;
                        while (map[cy-2][cx] != '#') {
                            cy--;
                            map[cy][cx] = '#';
                        }
                        if (map[cy+2][cx] != '#') break fill;
                        dir = 'R';
                        break;
                    case 'R':
                        if (map[cy][cx+2] == '#') break fill;
                        while (map[cy][cx+2] != '#') {
                            cx++;
                            map[cy][cx] = '#';
                        }
                        if (map[cy][cx-2] != '#') break fill;
                        dir = 'D';
                        break;
                    case 'D':
                        if (map[cy+2][cx] == '#') break fill;
                        while (map[cy+2][cx] != '#') {
                            cy++;
                            map[cy][cx] = '#';
                        }
                        if (map[cy-2][cx] != '#') break fill;
                        dir = 'L';
                        break;
                }
            }

            if (!first_output) 
                System.out.println("");
            printmap();
            first_output = false;
        }

    }

    void printmap() {
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++)
                System.out.print(map[i][j]);
            System.out.println("");
        }
    }

    public static void main(String[] args) {
        new Main().run();
    }
}

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.Arrays;


public class Main {
	private static  BufferedReader  br = null;

	static {
		br = new BufferedReader(new InputStreamReader(System.in));
	}

	/**
	* @param args
	*/
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int cnt = parseCnt();
		for (int i = 0; i < cnt; i++) {
			if (i != 0) {
				System.out.println();
			}
			parseSpiral();
		}
	}

	private static void parseSpiral() {
		int	len = parseCnt();
		if (len > 0) {
			char[][]	map = new char[len][len];

			if (len == 1) {
				map[0][0] = '#';
			} else {
				for (int i = 0, j = len-1; i <= j; i++,j--) {
					if (i%2 == 0) {
						if (i==0) {
							Arrays.fill(map[i], '#');
						} else {
							map[i] = map[j+2].clone();
							map[i][j+1] = ' ';
						}

						if (i != j) {
							map[j] = map[i].clone();
							map[j][i+1] = ' ';
						}
					} else {
						if (i==1) {
							Arrays.fill(map[i], ' ');
							map[i][0] ='#';
							map[i][j+1] ='#';
						} else {
							map[i] = map[j+2].clone();
							map[i][j+1] = '#';
						}

						if (i != j) {
							map[j] = map[i].clone();
							if (i+1 != j) {
								map[j][i+1] = '#';
							}
						}
					}
				}
			}

			for (int i = 0; i < map.length; i++) {
				System.out.println(map[i]);
			}
		}
	}

	private static int parseCnt() {
		int     cnt = 0;
		String  str = null;

		if ((str = parseStdin()) != null) {
			cnt = Integer.parseInt(str);
		}

		return cnt;
	}

	private static String parseStdin() {
		String	stdin = null;

		try {
			String	tmp = br.readLine();
			if (tmp != null) {
				if (!tmp.isEmpty()) stdin = tmp;
				}
			}
		catch (IOException e) {}

		return stdin;
	}
}

