	double EPS=1e-9;

	int m;

	void run(){
		for(int n=sc.nextInt(); n>0; n--){
			m=sc.nextInt();
			solve();
		}
	}

	void solve(){
		char[][] cs=new char[m][m];
		for(int k=0; k<m; k++){
			Arrays.fill(cs[k], ' ');
		}

		int i=-1, j=m-1;
		int a;
		for(int k=0;;){
			// up
			for(i++, a=0; j-k>=0; j--, a++)
				cs[j][i]='#';
			if(a<3)
				break;
			// right
			for(j++, a=0; i+k<m; i++, a++)
				cs[j][i]='#';
			if(a<3)
				break;
			// down
			for(i--, a=0; j+k<m; j++, a++)
				cs[j][i]='#';
			if(a<3)
				break;
			k+=2;
			// left
			for(j--, a=0; i-k>=0; i--, a++)
				cs[j][i]='#';
			if(a<3)
				break;
		}
		for(int k=0; k<m; k++){
			println(new String(cs[k]));
		}
	}

