namespace AOJ.Volume1
{
    public class SpiralPattern
    {
        public static int Main()
        {
            int dataset = int.Parse(Console.ReadLine());
            for (int d = 0; d < dataset; d++)
            {
                int n = int.Parse(Console.ReadLine());
                var sq = new string[n];
                // 初期化
                string line = new string('#', n);
                int cnt = n % 2 == 0 ? n / 2 : n / 2 + 1;

                // 上半分
                string bef = "";
                string aft = "";
                bool horizontal = true;
                for (int i = 0; i < cnt; i++)
                {
                    if (horizontal) { line = bef + new string('#', n - bef.Length - aft.Length) + aft; }
                    else
                    {
                        if (bef.Length + aft.Length > n) { line = bef + aft.Substring(1); }
                        else
                        {
                            line = bef + new string(' ', n - bef.Length - aft.Length) + aft;
                        }
                    }
                    sq[i] = line;
                    if (i % 2 == 0)
                    {
                        bef += "# ";
                        aft += " #";
                    }
                    horizontal = !horizontal;
                }

                // 下半分
                cnt = n / 2;
                bef = "# ";
                aft = "";
                horizontal = true;
                for (int i = 1; i <= cnt; i++)
                {
                    if (horizontal) { line = bef + new string('#', n - bef.Length - aft.Length) + aft; }
                    else
                    {
                        if (bef.Length + aft.Length > n)
                        {
                            line = bef + aft.Substring(1);
                        }
                        else
                        {
                            line = bef + new string(' ', n - bef.Length - aft.Length) + aft;
                        }
                    }
                    sq[n - i] = line;
                    if (i % 2 != 0)
                    {
                        if (n % 4 == 0 && i == cnt - 1) { aft += " #"; }
                        else
                        {
                            bef += "# ";
                            aft += " #";
                        }
                    }
                    horizontal = !horizontal;
                }

                for (int i = 0; i < n; i++)
                {
                    Console.WriteLine(sq[i]);
                }
            }
            return 0;
        }
    }
}

