using System;

public class Hello
{
    public static void Main()
    {
        var d = int.Parse(Console.ReadLine().Trim());
        for (int i = 0; i < d; i++)
        {
            var n = int.Parse(Console.ReadLine().Trim());
            var map = new int[n, n];
            for (int j = 0; j < n; j++) map[j, 0] = 1;
            writeSharp(map, n - 1, 2, 0, n , 0, 0);
            if (i > 0) Console.WriteLine();
            display(map);
        }
    }
    public static void display (int[,] map)
    {
        var n = map.GetLength(0);
        for (int i = 0; i < n; i++)
            for (int j = 0; j < n; j++)
            {
                if (map[i, j] == 0)
                    if (j == n - 1) Console.WriteLine(" ");
                    else Console.Write(" ");
                else
                    if (j == n - 1) Console.WriteLine("#");
                else Console.Write("#");
            }
    }
    public static void writeSharp(int[,] map, int bn, int bt, int ud, int n, int x, int y)
    {
        var a = map.GetLength(0);
        if (a % 2 == 0 && bn == 1 && bt == 1) return;
        if (a % 2 == 1 && bn == 0 && bt == 2) return;
        map[x, y] = 1;
        n--;
        if (n == 0)
        {
            bt--;
            if (bt == 0) { bn -= 2; bt = 2; }
            ud++; if (ud > 3) ud = 0;
            if (ud == 0) y++;
            else if (ud == 1) x++;
            else if (ud == 2) y--;
            else x--;
            n = bn;
        }
        else
        {
            if (ud == 0) y++;
            else if (ud == 1) x++;
            else if (ud == 2) y--;
            else x--;
        }
        writeSharp(map, bn, bt, ud, n, x, y);
    }
}

