using System;

namespace AOJ.Volume1
{
    public class SpiralPattern
    {
        public static int Main()
        {
            int dataset = int.Parse(Console.ReadLine());
            for (int d = 0; d < dataset; d++)
            {
                int n = int.Parse(Console.ReadLine());
                var sq = new string[n];
                // 初期化
                string line = new string('#', n);
                int cnt = n % 2 == 0 ? n / 2 : n / 2 + 1;

                // 上半分
                string bef = "";
                string aft = "";
                bool horizontal = true;
                for (int i = 0; i < cnt; i++)
                {
                    if (horizontal) { line = bef + new string('#', n - bef.Length - aft.Length) + aft; }
                    else
                    {
                        if (bef.Length + aft.Length > n) { line = bef + aft.Substring(1); }
                        else
                        {
                            line = bef + new string(' ', n - bef.Length - aft.Length) + aft;
                        }
                    }
                    sq[i] = line;
                    if (i % 2 == 0)
                    {
                        bef += "# ";
                        aft += " #";
                    }
                    horizontal = !horizontal;
                }

                // 下半分
                cnt = n / 2;
                bef = "# ";
                aft = "";
                horizontal = true;
                for (int i = 1; i <= cnt; i++)
                {
                    if (horizontal) { line = bef + new string('#', n - bef.Length - aft.Length) + aft; }
                    else
                    {
                        if (bef.Length + aft.Length > n)
                        {
                            line = bef + aft.Substring(1);
                        }
                        else
                        {
                            line = bef + new string(' ', n - bef.Length - aft.Length) + aft;
                        }
                    }
                    sq[n - i] = line;
                    if (i % 2 != 0)
                    {
                        if (n % 4 == 0 && i == cnt - 1) { aft += " #"; }
                        else
                        {
                            bef += "# ";
                            aft += " #";
                        }
                    }
                    horizontal = !horizontal;
                }

                for (int i = 0; i < n; i++)
                {
                    Console.WriteLine(sq[i]);
                }
                if (d != dataset - 1) { Console.WriteLine(); }
            }
            return 0;
        }
    }
}

function bomb(y,x,z){
   if(yx[y][x]!="+")return;
   var dy=[-1,0,1,0];
   var dx=[0,1,0,-1];
   var Y=y+dy[z];
   var X=x+dx[z];
   if(Y<0 || X<0 || Y>=n || X>=n){
      z=(z+1)%4;
      bomb(y,x,z);
   }else{
      if(yx[Y][X]=="#"){
         yx[Y-dy[z]][X-dx[z]]="+";
         if(z==3)bomb(Y-2*dy[z]-1,X-2*dx[z],0);
         else if(z==0)bomb(Y-2*dy[z],X-2*dx[z]+1,1);
         else if(z==1)bomb(Y-2*dy[z]+1,X-2*dx[z],2);
         else if(z==2)bomb(Y-2*dy[z],X-2*dx[z]-1,3);
      }else{
         yx[y][x]="#";
         last=[y,x];
         bomb(Y,X,z);
      }
   }
