	node no;
	for(int i=1;i<=n;i++) no.a[i]=n2.a[n1.a[i]];
	return no;
}
node ni(node n1){
	node no;
	for(int i=1;i<=n;i++) no.a[n1.a[i]]=i;
	return no;
}
node mi(node x,int t){
	node d;
	for(int i=1;i<=n;i++) d.a[i]=i;
	while(t){
		if(t%2) d=cheng(d,x);
		x=cheng(x,x);t/=2;
	}
	return d;
}
