Int N;
Int K;
vector<int> p;
vector<int> q;

vector<int> id(){
	vector<int> r(N);
	REP(i,N)r[i] = i;
	return r;
}

vector<int> comp(const vector<int> &p, const vector<int> &q){
	vector<int> r(N);
	REP(i,N)r[i] = p[q[i]];
	return r;
}
vector<int> inv(const vector<int> &p){
	vector<int> r(N);
	REP(i,N)r[p[i]] = i;
	return r;
}
vector<int> pow(const vector<int> &p, int k){
	if(k==0)return id();
	auto r = pow(p,k/2);
	r = comp(r,r);
	if(k%2==1)r = comp(r,p);
	return r;
}

void solve(){
	if(K==1){
		REP(i,N)fout<<p[i]+1<<" ";
		fout<<endl;
		return;
	}else if(K==2){
		REP(i,N)fout<<q[i]+1<<" ";
		fout<<endl;
		return;
	}
	vector<int> A, B;
	A.reserve(N);
	B.reserve(N);
	switch(K%6){
	case 0:
		A = comp(inv(q),p);
		break;
	case 1:
		A = p;
		break;
	case 2:
		A = q;
		break;
	case 3:
		A = comp(q,inv(p));
		break;
	case 4:
		A = inv(p);
		break;
	case 5:
		A = inv(q);
		break;
	default:
		assert(false);
	}
	int K2 = K-2;
	B = comp(comp(q,inv(p)),comp(inv(q),p));
	B = pow(B,K2/6);
	int rem = K2%6;
	do{
		if(rem<=1)break;
		B = comp(B,q);
		if(rem<=2)break;
		B = comp(B,inv(p));
		if(rem<=4)break;
		B = comp(B,inv(q));
	}while(0);
	auto r = comp(comp(B,A),inv(B));
	REP(i,N){
		fout<<r[i]+1;
		if(i<N-1){
			fout<<" ";
		}
	}
	fout<<endl;
}


//{{{ main function
int main(){
	fin >> N;
	fin >> K;
	p.assign(N,Int());
	for(int i = 0 ; i < N ; i++){
		fin >> p[i];
		p[i]--;
	}
	q.assign(N,Int());
	for(int i = 0 ; i < N ; i++){
		fin >> q[i];
		q[i]--;
	}
	solve();
	return 0;
}

