vi power(const vi& p, int k) {
  int n = p.size();
  vector<bool> vis(n);
  vi ret(n);
  for (int i = 0; i < n; ++i)
    if (!vis[i]) {
      vector<int> cycle;
      int x = i;
      while (!vis[x]) {
        cycle.push_back(x);
        vis[x] = true;
        x = p[x];
      }
      for (int i = 0; i < cycle.size(); ++i)
        ret[cycle[i]] = cycle[norm(i + k, cycle.size())];
    }
  return ret;
}

