vector<int> inverse(vector<int> a) {
  int n = (int) a.size();
  vector<int> ret(n);
  for (int i = 0; i < n; i++) {
    ret[a[i]] = i;
  }
  return ret;
}

vector<int> multiple(vector<int> a, vector<int> b) {
  int n = (int) a.size();
  vector<int> ret(n);
  for (int i = 0; i < n; i++) {
    ret[i] = a[b[i]];
  }
  return ret;
}

vector<int> power(vector<int> a, int b) {
  int n = (int) a.size();
  vector<int> ret(n);
  iota(ret.begin(), ret.end(), 0);
  while (b) {
    if (b & 1) {
      ret = multiple(ret, a);
    }
    b >>= 1;
    ret = multiple(ret, ret);
  }
  return ret;
}

