#include <bits/stdc++.h>
using namespace std;

struct perm {
	vector <int> v;
	void input(int n) {
		v.resize(n);
		for (int i = 0; i < n; i++) {
			scanf("%d", &v[i]);
			v[i]--;
		}
	}
	void output() {
		for (int i = 0; i < v.size(); i++)
			printf("%d ", v[i] + 1);
		puts("");
	}
	int& operator [](int id) {
		return v[id];
	}
};

perm inv(perm a) {
	vector <int> res(a.v.size(), 0);
	for (int i = 0; i < res.size(); i++)
		res[a[i]] = i;
	return {res};
}

perm operator *(perm a, perm b) {
	vector <int> res(a.v.size(), 0);
	for (int i = 0; i < a.v.size(); i++)
		res[i] = a[b[i]];
	return {res};
}

perm operator ^(perm a, int n) {
	perm res;
	for (int i = 0; i < a.v.size(); i++)
		res.v.push_back(i);
	for (; n; n /= 2) {
		if (n % 2)
			res = res * a;
		a = a * a;
	}
	return res;
}

int N, K;
perm p, q;

void load() {
	scanf("%d%d", &N, &K);
	p.input(N);
	q.input(N);
}

perm solve() {
	for (; K > 2 && K % 3 != 2; K--) {
		perm t = q;
		q = q * inv(p);
		p = t;
	}

	if (K == 1)
		return p;
	if (K == 2)
		return q;

	int diff = K / 3 + 1;
	int same = K / 3 - 1;
	perm pref = q * inv(p) * inv(q) * p;
	pref = pref ^ (diff / 2);
	if (diff % 2)
		pref = pref * q * inv(p);
	perm a = inv(q) * inv(p);
	perm b = q * p;
	if (same % 2) 
		swap(a, b);
	perm suff = a * b;
	suff = suff ^ (same / 2);
	if (same % 2)
		suff = suff * a;
	return pref * suff * inv(q);
}

int main() {
	load();
	solve().output();
	return 0;
}

