vector<int> power(const vector<int> &v, int k) {
    vector<int> res(v.size());
    for (int i = 0; i < res.size(); ++i) {
        res[i] = i;
    }
    if (k == 0) {
        return res;
    }
    --k;
    vector<char> used(v.size());
    for (int i = 0; i < v.size(); ++i) {
        if (!used[i]) {
            int pos = i;
            vector<int> q;
            while (!used[pos]) {
                q.push_back(v[pos]);
                used[pos] = 1;
                pos = v[pos];
            }
            vector<int> w(q.size());
            for (int i = 0; i < w.size(); ++i) {
                w[i] = (q[(i + k) % w.size()]);
            }
            reverse(w.begin(), w.end());
            while (!w.empty()) {
                res[pos] = w.back();
                w.pop_back();
                pos = v[pos];
            }
        }
    }
    return res;
}

