struct Perm {
  VI data;

  Perm(int sz) : data(sz) {}
  Perm(VI x) : data(x) {}

  Perm operator*(const Perm &rhs) const {
    VI ans(SZ(data));
    for (int i = 0; i < SZ(data); ++i) {
      ans[i] = data[rhs.data[i]];
    }
    return ans;
  }

  Perm Inv() const {
    VI ans(SZ(data));
    for (int i = 0; i < SZ(data); ++i) {
      ans[data[i]] = i;
    }
    return ans;
  }

  Perm Pow(int k) const {
    Perm ans(SZ(data));
    for (int i = 0; i < SZ(data); ++i) { ans.data[i] = i; }
    Perm coef = *this;

    while (k) {
      if (k & 1) { ans = ans * coef; }
      k >>= 1;
      coef = coef * coef;
    }
    return ans;
  }
};

void Out(const Perm &p) {
  for (int v : p.data) { cout << v + 1 << " "; }
  cout << "\n";
}

