    cin >> q.data[i]; --q.data[i];
  }

  if (K == 1) { Out(p); return 0; }
  if (K == 2) { Out(q); return 0; }

  const int njumps = (K - 2) / 6;
  Perm coef = (q * p.Inv() * q.Inv() * p).Pow(njumps);

  Perm a = coef * p * coef.Inv();
  Perm b = coef * q * coef.Inv();
  int cur_b = 6 * njumps + 2;

  while (cur_b < K) {
    tie(a, b) = make_pair(b, b * a.Inv());
    ++cur_b;
  }

  Out(b);
}

