struct permu{
	vector < int > p;
	void read(){
		p.resize(n);
		int i;
		for(i=0;i<n;++i){
			scanf("%d",&p[i]);
			--p[i];
		}
	}
	void print(){
		int i;
		for(i=0;i<n;++i){
			printf("%d ",p[i]+1);
		}
		printf("\n");
	}
	void init(){
		p.resize(n);
		int i;
		for(i=0;i<n;++i) p[i]=i;
	}
};
permu M(permu A,permu B){
	permu ret;
	ret.init();
	int i;
//	printf("%d %d\n",(int)A.p.size(),(int)B.p.size());
	for(i=0;i<n;++i){
		ret.p[i]=B.p[A.p[i]];
	}
	return ret;
}
permu P(permu A,int T){
	permu R;
	R.init();
	while(T){
		if(T&1) R=M(R,A);
		T>>=1;
		A=M(A,A);
	}
	return R;
}
permu inv(permu A){
	permu ret;
	ret.init();
	int i;
	for(i=0;i<n;++i) ret.p[A.p[i]]=i;
	return ret;
}
permu p,q,ip,iq;
permu eval(int v){
	if(v==1)
		return p;
	else if(v==-1)
		return ip;
	else if(v==2)
		return q;
	else
		return iq;
}
int getsz(int m){
	if(m==1) return 1;
	int id=(m+1)/3;
	return (id-1)*4+1+(m+1)%3;
}
int main(){
	int i,j,k;
	scanf("%d%d",&n,&m);
	p.read();
	q.read();
	ip=inv(p);
	iq=inv(q);
	int S=getsz(m);
//	printf("S=%d\n",S);
	permu ALLP=M(eval(fhp[0]),M(eval(fhp[1]),M(eval(fhp[2]),eval(fhp[3]))));
	permu LLL=P(ALLP,((S&1)?(S/2):(S/2-1))/4),MMM=eval(fmd[(m-1)%6]),RRR;
	int t=(S/2)%4;
	for(i=0;i<t;++i) LLL=M(LLL,eval(fhp[i]));
	RRR=inv(LLL);
	if(S&1)
		M(M(LLL,MMM),RRR).print();
	else if(m&1)
		M(LLL,M(M(eval(-1),eval(2)),RRR)).print();
	else
		M(LLL,M(M(eval(1),eval(-2)),RRR)).print();
	return 0;
}

