typedef vector<ll> vii;

vii inv(const vii &A) {
  vii B = A;
  reverse(all(B));
  for (auto &x : B)
    x *= -1;
  return B;
}

vii comb(const vii &A, const vii &B) {
  vii C = A;
  for (int x : B) {
    if (!C.empty() && C.back() == -x) {
      C.pop_back();
    } else {
      C.push_back(x);
    }
  }
  return C;
}

