void rem_same(vii &A, vii &B) {
  while (!A.empty() && !B.empty() && A.back() == B.back()) {
    A.pop_back();
    B.pop_back();
  }
}

void diff(vii A, vii B) {
  for (int i = 0; i < 2; ++i) {
    rem_same(A, B);
    reverse(all(A));
    reverse(all(B));
  }
  assert(A.empty());
  //print(A);
  print(B);
  //cerr << endl;
}


typedef vector<int> Perm;

