int n;

Perm inverse(const Perm &p) {
  Perm q(n);
  for (int i = 0; i < n; ++i) {
    q[p[i]] = i;
  }
  return q;
}

Perm comb(const Perm &p, const Perm &q) {
  Perm s(n);
  for (int i = 0; i < n; ++i)
    s[i] = p[q[i]];
  return s;
}

Perm id() {
  Perm p(n);
  iota(all(p), 0);
  return p;
}

Perm pow(Perm p, ll to) {
  Perm q = id();
  while (to) {
    if (to & 1) {
      q = comb(q, p);
    }
    p = comb(p, p);
    to >>= 1;
  }
  return q;
}

vii my_prod(int n) {
  if (n == 1) {
    return {1};
  } else if (n == 2) {
    return {2};
  }

  vii L = {2}, R = {};
  for (int i = 3; i <= n; ++i) {
    if (i % 6 == 3) {
      L.push_back(-1);
    } else if (i % 6 == 4) {
      R.push_back(-2);
    } else if (i % 6 == 5) {
      L.push_back(-2);
      R.push_back(1);
    } else if (i % 6 == 0) {
      L.push_back(1);
    } else if (i % 6 == 1) {
      R.push_back(2);
    } else if (i % 6 == 2) {
      L.push_back(2);
      R.push_back(-1);
    }
  }

  vii ans = L;
  reverse(all(R));
  for (int x : R)
    ans.push_back(x);

  return ans;
}

Perm get_naive(Perm p, Perm q, int k) {
  if (k == 1) {
    return p;
  } 
  for (int i = 3; i <= k; ++i) {
    p = comb(q, inverse(p));
    swap(p, q);
  }
  return q;
}

Perm get(Perm p, Perm q, ll k) {
  if (k == 1) {
    return p;
  } else if (k == 2) {
    return q;
  }

  Perm x = comb(inverse(p), comb(inverse(q), comb(p, q)));
  Perm y = comb(inverse(p), comb(q, comb(p, inverse(q))));

  Perm L = q;
  Perm R = id();

  ll times = (k - 2) / 6;
  L = comb(L, pow(x, times));
  R = pow(y, times);


  for (int i = 6 * times + 3; i <= k; ++i) {
    if (i % 6 == 3) {
      //L.push_back(-1);
      L = comb(L, inverse(p));
    } else if (i % 6 == 4) {
      //R.push_back(-2);
      R = comb(inverse(q), R);
    } else if (i % 6 == 5) {
      //L.push_back(-2);
      L = comb(L, inverse(q));
      //R.push_back(1);
      R = comb(p, R);
    } else if (i % 6 == 0) {
      //L.push_back(1);
      L = comb(L, p);
    } else if (i % 6 == 1) {
      //R.push_back(2);
      R = comb(q, R);
    } else if (i % 6 == 2) {
      //L.push_back(2);
      L = comb(L, q);
      //R.push_back(-1);
      R = comb(inverse(p), R);
    }
  }

  //print(L);
  //print(R);

  Perm ans = comb(L, R);

  return ans;
}

