const int MN = 100005;

int N, K;
struct per {
	int a[MN];
	per() {}
	int & operator [] (const int &i) { return a[i]; }
	friend per operator ~ (per A) {
		per C;
		for (int i = 1; i <= N; ++i) C[A[i]] = i;
		return C;
	}
	friend per operator * (per A, per B) {
		per C;
		for (int i = 1; i <= N; ++i) C[i] = A[B[i]];
		return C;
	}
	friend per operator ^ (per B, int E) {
		per A;
		for (int i = 1; i <= N; ++i) A[i] = i;
		for (; E; E >>= 1, B = B * B)
			if (E & 1) A = A * B;
		return A;
	}
} p, q, ip, iq, qip, iqp, qipiqp, ans;

int main() {
	scanf("%d%d", &N, &K);
	for (int i = 1; i <= N; ++i) scanf("%d", &p[i]);
	for (int i = 1; i <= N; ++i) scanf("%d", &q[i]);
	ip = ~p, iq = ~q;
	qip = q * ip, iqp = iq * p;
	qipiqp = qip * iqp;
	ans = qipiqp ^ (K / 6);
	if (K % 6 == 0) ans = ans * iqp * ~ans;
	if (K % 6 == 1) ans = ans * p * ~ans;
	if (K % 6 == 2) ans = ans * q * ~ans;
	if (K % 6 == 3) ans = ans * qip * ~ans;
	if (K % 6 == 4) ans = ans * qip * iq * ~ans;
	if (K % 6 == 5) ans = ans * qip * iqp * iq * ~ans;
	for (int i = 1; i <= N; ++i) printf("%d%c", ans[i], " \n"[i == N]);
	return 0;
}

