// c = a . b
void mult(vector<int>& c, const vector<int> &a, const vector<int>& b)
{
	const int n = a.size();
	for(int i = 0; i < n; ++i) {
		c[i] = a[b[i]];
	}
}

// b = a
void copy(vector<int> &b, const vector<int> &a)
{
	const int n = a.size();
	for(int i = 0; i < n; ++i) {
		b[i] = a[i];
	}
}

void rev(vector<int> &b, const vector<int> &a)
{
	const int n = a.size();
	for(int i = 0; i < n; ++i) {
		b[a[i]] = i;
	}
}

void unit(vector<int>& a)
{
	const int n = a.size();
	for(int i = 0; i < n; ++i) {
		a[i] = i;
	}
}

// b = a ^ k
void power(vector<int>& b, const vector<int> &a, const int k)
{
	static vector<int> temp, a2;
	const int n = a.size();

	a2.resize(n);
	temp.resize(n);
	copy(a2, a);
	unit(b);

	for(int x = 0; x < 31; ++x) {

		if((k >> x) & 1) {
			mult(temp, b, a2);
			copy(b, temp);
		}

		mult(temp, a2, a2);
		copy(a2, temp);
	}
}

void print_answer(const vector<int>& xs)
{
	const int n = xs.size();
	for(int i = 0; i < n; ++i) {
		printf("%d\n", xs[i] + 1);
	}
}

int main()
{
	int n, k;
	vector<int> ps, qs;

	scanf("%d%d", &n, &k);
	ps.resize(n);
	qs.resize(n);
	for(int i = 0; i < n; ++i) {
		scanf("%d", &ps[i]);
		ps[i] -= 1;
	}
	for(int i = 0; i < n; ++i) {
		scanf("%d", &qs[i]);
		qs[i] -= 1;
	}

	if(k == 1) {
		print_answer(ps);
		return 0;
	} else if(k == 2) {
		print_answer(qs);
		return 0;
	}

	int sec = k / 3;
	int rem = k % 3;

	int first = (sec - 1) * 2 + rem;
	int second = (rem == 0) ? 2 : 1;

	// fprintf(stderr, "first = %d, second = %d\n", first, second);

	vector<int> f[4];

	for(int i = 0; i < 4; ++i) {
		f[i].resize(n);
	}

	copy(f[0], qs);
	rev(f[1], ps);
	rev(f[2], qs);
	copy(f[3], ps);

	vector<int> g(n), temp(n);

	unit(g);
	for(int i = 0; i < 4; ++i) {
		mult(temp, g, f[i]);
		copy(g, temp);
	}

	power(temp, g, first / 4);

	copy(g, temp);
	for(int i = 0; i < first % 4; ++i) {
		mult(temp, g, f[i]);
		copy(g, temp);
	}

	vector<int> rg(n);
	rev(rg, g);

	vector<int> h(n);

	unit(h);
	for(int i = 0; i < second; ++i) {
		mult(temp, h, f[(first % 4 + i) % 4]);
		copy(h, temp);
	}

	vector<int> ans(n);

	mult(temp, g, h);
	mult(ans, temp, rg);

	print_answer(ans);

	return 0;
}

