vector<int> identity(int n) {
	vector<int> A(n);
	for(int i=0; i<n; i++) {
		A[i] = i;
	}
	return A;
}

vector<int> inverse(vector<int> A) {
	vector<int> B(A.size());
	for(int i=0; i<(int)B.size(); i++) {
		B[A[i]] = i;
	}
	return B;
}

vector<int> multiply(vector<int> A, vector<int> B) {
	vector<int> C(A.size());
	for(int i=0; i<(int)C.size(); i++) {
		C[i] = A[B[i]];
	}
	return C;
}

vector<int> power(vector<int> A, int n) {
	if(n == 0) return identity(A.size());
	vector<int> B = power(A, n >> 1);
	B = multiply(B, B);
	if(n & 1) B = multiply(B, A);
	return B;
}

