    output(b);
  } else {
    m -= 2;
    vector<int> A = inv(a);
    vector<int> B = inv(b);
    vector<int> res = power(b * A * B * a, m / 6);
    int rm = m % 6;
    if (rm == 0) res *= b;
    if (rm == 1) res *= b * A;
    if (rm == 2) res *= b * A * B;
    if (rm == 3) res *= b * A * B * a * B;
    if (rm == 4) res *= b * A * B * a * a * b;
    if (rm == 5) res *= b * A * B * a * b * a * B;
    res *= power(A * b * a * B, m / 6);
    output(res);
  }
  return 0;
}

