class DASequenceOfPermutations {
	vector<int> random;
	int n;
	const ll MOD = 1e9 + 7;

	int hash(const vector<int> &p) {
		ll res = 0;
		fori(i, n) {
			res *= n;
			res += p[i];
			res %= MOD;
		}
		return res;
	}

	void op(const vector<int> &p, const vector<int> &q, vector<int> &res) {
		fori(i, n) res[p[i]] = q[i];
	}

	void print(io::output_writer &out, vector<int> &v) {
		fori(i, n) v[i]++;
		out << v;
	}

	void solve(io::input_reader &in, io::output_writer &out) {
		int k;
		in.read(n, k);
		vector<int> a[2];
		a[0].resize(n), a[1].resize(n);
		in >> a[0] >> a[1];
		fori(i, n) a[0][i]--, a[1][i]--;
		srand(0);
		random.resize(n);
		for (int &i:random) i = rand() % (2 * n);
		map<pair<int, int>, int> memos;
		pair<int, int> h;
		h.first = hash(a[0]);
		h.second = hash(a[1]);
		memos[h] = 1;
		int currentK = 1;
		vector<int> res(n);
		bool jump1 = false;
		while (true) {
			if (currentK + 1 >= k) {
				print(out, a[k - currentK]);
				return;
			}
			op(a[0], a[1], res);
			std::swap(a[0], a[1]);
			std::swap(a[1], res);
			currentK++;
			if (!jump1) {
				h.first = h.second;
				h.second = hash(a[1]);
				if (memos.find(h) != memos.end()) {
					int lastSeen = memos[h];
					int _k = k - lastSeen;
					int _cur = currentK - lastSeen;
					currentK = lastSeen + ((_k) / _cur) * _cur;
				}
				memos[h] = currentK;
			}
		}
	}

public:
	void solve(std::istream &in, std::ostream &out) {
		io::input_reader input(in);
		io::output_writer output(out);
		solve(input, output);
	}
};


