inline void inv(int *f, int *s)
{
	static int S[MAXN + 5];

	for(int i = 1; i <= n; ++i) S[f[i]] = i;
	for(int i = 1; i <= n; ++i) s[i] = S[i];
}

inline void mul(int *f, int *g, int *s)
{
	static int S[MAXN + 5];

	for(int i = 1; i <= n; ++i) S[i] = f[g[i]];
	for(int i = 1; i <= n; ++i) s[i] = S[i];
}

inline void fpm(int *f, int y, int *s)
{
	if(y == 0) { for(int i = 1; i <= n; ++i) s[i] = i; return; }

	static int S[MAXN + 5], vis[MAXN + 5];
	for(int i = 1; i <= n; ++i) vis[i] = 0;

	for(int i = 1; i <= n; ++i) if(!vis[i])
	{
		static int cir[MAXN + 5], len;
		len = 0;

		for(int j = i; !vis[j]; j = f[j]) vis[j] = 1, cir[len++] = j;
		for(int j = 0; j < len; ++j) S[cir[j]] = cir[(j + y) % len];
	}
	for(int i = 1; i <= n; ++i) s[i] = S[i];
}

inline void solve()
{
	static int A[MAXN + 5], iA[MAXN + 5], ip[MAXN + 5], iq[MAXN + 5], M[7][MAXN + 5];

	inv(p, ip);
	inv(q, iq);
	mul(q, ip, A);
	mul(A, iq, A);
	mul(A, p, A);
	inv(A, iA);

	int m = k % 6 == 0 ? k / 6 - 1 : k / 6, t = k % 6 == 0 ? 6 : k % 6;
	fpm(A, m, A);
	for(int i = 1; i <= n; ++i) M[1][i] = p[i];
	for(int i = 1; i <= n; ++i) M[2][i] = q[i];
	for(int i = 3; i <= 6; ++i)
	{
		static int f[MAXN + 5];

		inv(M[i - 2], f);
		mul(M[i - 1], f, M[i]);
	}
	fpm(iA, m, iA);

	mul(A, M[t], A);
	mul(A, iA, A);

	for(int i = 1; i <= n; ++i) printf("%d ", A[i]);
	puts("");
}

