struct P{
	int *a;

	P(){
		a = new int[n+1];
		for(int i=1;i<=n;i++) a[i]=i;
	}

	int & operator[](int i){
		return a[i];
	}

	P operator*(P x){
		P t;
		for(int i=1;i<=n;i++) t[i] = x[a[i]];
		return t;
	}
}p,q;

P T(P p){
	P q;
	for(int i=1;i<=n;i++) q[p[i]]=i;
	return q;
}

P getPow(P x, int y){
	P res;
	while(y){
		if(y&1) res=res*x;
		x=x*x;
		y>>=1;
	}
	return res;
}

P gao(int k){
	P t = p*T(q)*T(p)*q;
	t = getPow(t, k/6);
	p = T(t)*p*t;
	q = T(t)*q*t;
	k %= 6;
	while(k--){
		t = T(p)*q;
		p=q; q=t;
	}
	return p;
}

