    beki.resize(35);
    beki[0] = pqpq;
    for (int i = 0; i+1 < 35; ++i) beki[i+1] = beki[i] + beki[i];

    // 
    auto res = solve(K);
    for (auto v : res) cout << v + 1 << " ";
    cout << endl;
}

