vector<int> powPerm(vector<int> p, int k) {
  vector<int> res(n);
  for (int i = 0; i < n; i++) {
    res[i] = i;
  }
  while (k) {
    if (k & 1) {
      res = mul(res, p);
    }
    k >>= 1;
    p = mul(p, p);
  }
  return res;
}

