void solve() {
  if (k == 0) {
    printAns(p);
    return;
  }
  if (k == 1) {
    printAns(q);
    return;
  }
  if (k == 2) {
    printAns(mul(q, inv(p)));
    return;
  }
  vector<int> a;
  {
    int x = k / 3;
    a = powPerm(mul(mul(q, inv(p)), mul(inv(q), p)), x / 2);
    if (x & 1) {
      a = mul(a, mul(mul(q, inv(p)), inv(q)));
    } else {
      a = mul(a, q);
    }
  }
  vector<int> b;
  {
    int x = (k - 3) / 6;
    b = powPerm(mul(mul(inv(p), q), mul(p, inv(q))), x);
    int y = (k - 3) % 6;
    if (y == 1) {
      b = mul(mul(p, inv(q)), b);
    } else if (y == 2) {
      b = mul(mul(mul(p, p), inv(q)), b);
    } else if (y == 3) {
