perm mul(const perm & a, const perm & b){
	perm q(a.size());
	F(i, 0, n)
		q[i] = a[b[i]];
	return q;
}

perm inv(const perm & a){
	perm b(a.size());
	F(i, 0, n)
		b[a[i]] = i;
	ret b;
}

void finish(const perm & p){
	I(x, p)cout << x + 1 << ' ';
	exit(0);
}

int wc[N];
int nxt[N];
int cycle[N];

perm pw(const perm & a, int w){
	if(w == 0){
		vector<int>res(a.size());
		iota(all(res), 0);
		ret res;
	}
//	F(i, 0, n)cerr << a[i] << ' ';
//	cerr << endl;
	vector<int> res(a.size());
	fill(wc, wc + n, 0);
	F(i, 0, n){
		nxt[i] = a[i];
		cycle[i] = 0;
	}
	F(i, 0, n){
		if(wc[i])cont;
		int j = i;
		wc[i] = 1;
		cycle[i] = 1;
		j = nxt[i];
		while(j != i){
			++cycle[i];
			wc[j] = 1;
			j = nxt[j];
		}
	}
	F(i, 0, n){
		if(wc[i] == 2)cont;
		int len = w%cycle[i];
		int j = i;
		F(t, 0, len)j = nxt[j];
		res[i] = j;
		int t = nxt[i];
		wc[i] = 2;
		while(t != i){
			wc[t] = 2;
			j = nxt[j];
			res[t] = j;
			t = nxt[t];
		}
	}
	return res;
}

