		R = mul(R, p);
		R = mul(R, iq);
		R = pw(R, k/6);
		finish(mul(L, R));
	}
	if(r == 5){
		L = pw(L, k/6 + 1);
		L = mul(L, iq);
		auto R = mul(ip, q);
		R = mul(R, p);
		R = mul(R, iq);
