using namespace std;

struct Perm : vector<int> {
    using vector<int>::vector;
    Perm() {}
    Perm(initializer_list<int> list) : vector<int>(list) {}

};

Perm& operator*=(Perm& a, const Perm& b) {
    assert(a.size() == b.size());
    int n = a.size();
    Perm _a(a);
    for (int i = 0; i < n; i++) {
        a[i] = _a[b[i]];
    }
    return a;
}
Perm operator*(const Perm& a, const Perm& b) {
    assert(a.size() == b.size());
    int n = a.size();
    Perm c(n);
    for (int i = 0; i < n; i++) {
        c[i] = a[b[i]];
    }
    return c;
}
Perm pinv(const Perm& a) {
    int n = a.size();
    Perm c(n);
    for (int i = 0; i < n; i++) {
        c[a[i]] = i;
    }
    return c;
}
Perm& operator/=(Perm& a, const Perm& b) {
    return a *= pinv(b);
}
Perm operator/(const Perm& a, const Perm& b) {
    return a * pinv(b);
}
Perm ppow(Perm a, int64_t e) {
    if (e < 0) {
        a = pinv(a);
        e *= -1;
    }
    int n = a.size();
    Perm c(n);
    iota(c.begin(), c.end(), 0);
    for (; e; e>>=1) {
        if (e&1) c *= a;
        a *= a;
    }
    return c;
}
int cycles_cnt(Perm a) {
    int cnt = 0, n = a.size();
    for (int i = 0; i < n; i++) {
        if (~a[i]) {
            cnt++;
            for (int j = i; ~a[j];) {
                int nxt = a[j];
                a[j] = -1;
                j = nxt;
            }
        }
    }
    return cnt;
}

void solve() {
    int n, k;
    cin >> n >> k;
    Perm p(n), q(n);
    for (int i = 0; i < n; i++) {
        cin >> p[i];
        p[i]--;
    }
    for (int i = 0; i < n; i++) {
        cin >> q[i];
        q[i]--;
    }
    Perm ip = pinv(p);
    Perm iq = pinv(q);
    int exp = (k-1) / 6;
    int rem = (k-1) % 6;
    Perm base = q*ip*iq*p;
    Perm lhs = ppow(base, exp);
    Perm rhs = pinv(lhs);
    Perm mid;
    if      (rem == 0) mid = p;
    else if (rem == 1) mid = q;
    else if (rem == 2) mid = q*ip;
    else if (rem == 3) mid = q*ip*iq;
    else if (rem == 4) mid = q*ip*iq*p*iq;
    else if (rem == 5) mid = q*ip*iq*p*p*iq;
    Perm res = lhs * mid * rhs;
    for (int i = 0; i < n; i++) {
        cout << res[i]+1 << ' ';
    }
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);
    solve();
    cout << endl;
}

