#include <bits/stdc++.h>
using namespace std;
typedef long long ll;

ll n, k;
vector<int> p, q, ip, iq, tot1, tot2, id;

vector<int> mul(vector<int> a, vector<int> b) {
	vector<int> ans;
	ans.resize(n);
	for (int i = 0; i < n; ++i)
		ans[i] = a[b[i]];
	return ans;
}

vector<int> inv(vector<int> a) {
	vector<int> ans;
	ans.resize(n);
	for (int i = 0; i < n; ++i)
		ans[a[i]] = i;
	return ans;
}

vector<int> exp(vector<int> a, int j) {
	if (j == 0)
		return id;
	if (j%2 == 1)
		return mul(a, exp(a, j-1));
	auto b = exp(a, j/2);
	return mul(b, b);
}

vector<int> get1(ll j) {
	ll l = 2*(j-1)/3;
	if (j%3 != 1)
		l += 2;
	auto ans = exp(tot1, l/4);
	if (l%4 == 0)
		return ans;
	if (l%4 == 1)
		return mul(ans, q);
	if (l%4 == 2)
		return mul(ans, mul(q, ip));
	if (l%4 == 3)
		return mul(ans, mul(q, mul(ip, iq)));
}

vector<int> get2(ll j) {
	ll l = 2*(j-1)/3;
	if (j%3 != 0)
		l--;
	auto ans = exp(tot2, l/4);
	if (l%4 == 0)
		return ans;
	if (l%4 == 1)
		return mul(iq, ans);
	if (l%4 == 2)
		return mul(p, mul(iq, ans));
	if (l%4 == 3)
		return mul(q, mul(p, mul(iq, ans)));
}

int main() {
	ios_base::sync_with_stdio(0); cin.tie(0); cout.tie(0);

	cin >> n >> k;
	p.resize(n);
	q.resize(n);
	for (int i = 0; i < n; ++i) {
		cin >> p[i]; p[i]--;
	}
	for (int i = 0; i < n; ++i) {
		cin >> q[i]; q[i]--;
	}
	id.resize(n);
	for (int i = 0; i < n; ++i)
		id[i] = i;

	ip = inv(p);
	iq = inv(q);
	tot1 = mul(q, mul(ip, mul(iq, p)));
	tot2 = mul(ip, mul(q, mul(p, iq)));

	vector<int> ans;
	if (k == 1)
		ans = p;
	else if (k == 2)
		ans = q;
	else if (k == 3)
		ans = mul(q, ip);
	else
		ans = mul(get1(k), get2(k));
	for (int i = 0; i < n; ++i)
		cout << ans[i]+1 << ' ';
	cout << endl;
}

