}

vector<int> inverse(vector<int> a){
    int n = a.size();
    vector<int> res(n);
    for(int i = 0;i < n;i++){
        res[a[i]] = i;
    }
    return res;
}

vector<int> f(vector<int> a, vector<int> b){
    return permutate(inverse(a), b);
}

vector<int> id;

vector<int> power(vector<int> f, int x){
    vector<int> res = id;
    vector<int> tmp = f;
    for(int i = 0;i <= 30;i++){
        if((x >> i) % 2 == 1){
            res = permutate(res, tmp);
        }
        tmp = permutate(tmp, tmp);
    }
    return res;
}

