int main(){
    cin >> n >> k;
    P.resize(n);
    Q.resize(n);
    for(int i = 0;i < n;i++)id.push_back(i);
    for(int i = 0;i < n;i++){cin >> P[i];P[i]--;}
    for(int i = 0;i < n;i++){cin >> Q[i];Q[i]--;}
    p = inverse(P);
    q = inverse(Q);

    vector<int> l = permutate(permutate(q, P), permutate(Q, p));
    vector<int> r = permutate(permutate(P, q), permutate(p, Q));

    vector<int> a = P;
    vector<int> b = Q;

    int ind = 1;
    while(ind < k && ind < 7){
        a = f(a, b);
        swap(a, b);
        ind++;
    }
    while((k - ind) % 6 != 0){
        a = f(a, b);
        swap(a, b);
        ind++;
    }
    if(k == ind){
        show(a);
        return 0;
    }
    a = permutate(power(l, (k-ind) / 6), a);
    a = permutate(a, power(r, (k-ind) / 6));
    show(a);
    return 0;
}

