typedef long long int int64;


const int N = (int) 1e5 + 100;
int n;

struct Perm
{
    int p[N];

    Perm() : p() 
    {
        for (int i = 0; i < N; i++)
            p[i] = i;
    }

    void read()
    {
        for (int i = 0; i < n; i++)
        {
            scanf("%d", &p[i]);
            p[i]--;
        }
    }

    Perm inv() const
    {
        Perm ans = Perm();
        for (int i = 0; i < n; i++)
            ans.p[p[i]] = i;
        return ans;
    }

    Perm operator * (const Perm &A) const
    {
        Perm ans = Perm();
        for (int i = 0; i < n; i++)
            ans.p[i] = A.p[p[i]];
        return ans;
    }

    Perm fpow(int d)
    {
        if (d == 0)
            return Perm();
        Perm A = fpow(d / 2);
        A = A * A;
        if (d & 1)
            A = A * *this;
        return A;
    }

    void print() const
    {
        for (int i = 0; i < n; i++)
            printf("%d ", p[i] + 1);
        printf("\n");
    }

};

Perm A, B, a, b, bABa;

Perm getPref(int pref)
{
    Perm ans = bABa.fpow(pref / 4);
    if (pref % 4 >= 1)
        ans = ans * b;
    if (pref % 4 >= 2)
        ans = ans * A;
    if (pref % 4 >= 3)
        ans = ans * B;
    return ans;
}

int getSufLen(int x)
{
    int ans = x - (x + 2) / 3;
    return ans;
}

int getPrefLen(int x)
{
    int ans = x - x / 3 - 2;
    return ans;
}

