    }


    int k;
    scanf("%d%d", &n, &k);
    A.read();
    B.read();

    if (n == 1)
    {
        A.print();
        return 0;
    }
    if (n == 2)
    {
        B.print();
        return 0;
    }
    int suff = getSufLen(k);
