        REP(i, 0, N) {
            if (p[i] != base[i] || q[i] != next[i]) {
                ok = false;
            }
        }
        if (ok) {
            break;
        }
    }
    //ll nums[ind][N];
    REP(i, 0, N) {
        nums[0][i] = p[i];
        nums[1][i] = q[i];
    }
    REP(i, 2, ind + 2) {
        REP(j, 0, N) {
            nums[i][nums[i - 2][j] - 1] = nums[i - 1][j];
        }
    }
    // cout << ind << endl;
    // REP(i, 0, ind + 2) {
    //     REP(j, 0, N) {
    //         cout << nums[i][j] << " ";
    //     }
    //     cout << endl;
    // }
    ll rem = (K - 1) % ind;
    REP(i, 0, N) {
        cout << nums[rem][i];
        if (i != N - 1) {
            cout << " ";
        }
    }
    cout << endl;
}

