		for(int i=1;i<=a.size();i++)
			ans[a[i]]=i;
		return ans;
	}
	int vis[1000001];
	void find_cycle(perm a,int x,vector<int>&rem)
	{
		if(vis[x]) return;
		vis[x]=1;
		rem.push_back(x);
		find_cycle(a,a[x],rem);
	}
	perm Pow(perm a,int x)
	{
		perm ans=e(a.size());
		vector<vector<int> >cycle;
		memset(vis,0,sizeof(vis));
		for(int i=1;i<=a.size();i++)
		{
			if(!vis[i])
			{
				vector<int> rem;
				find_cycle(a,i,rem);
				cycle.push_back(rem);
			}
		}
		for(int i=0;i<cycle.size();i++)
		{
			for(int j=0;j<cycle[i].size();j++)
				ans[cycle[i][j]]=cycle[i][(j+x)%cycle[i].size()];
		}
