using namespace std;

struct permutation {
  vector<int> p;
  int n;

  void init(int _n) {
    n = _n;
    p.reserve(n + 1);
    for(int i = 1; i <= n; i++) {
      p[i] = i;
    }
  }

  void read(int _n) {
    init(_n);
    for(int i = 1; i <= n; i++) {
      scanf("%d", &p[i]);
    }
  }

  void print(void) {
    for(int i = 1; i <= n; i++) {
      printf("%d%c", p[i], i == n ? '\n' : ' ');
    }
  }

  permutation inverse(void) {
    permutation q;
    q.init(n);
    for(int i = 1; i <= n; i++) {
      q.p[p[i]] = i;
    }
    return q;
  }

  permutation operator * (const permutation &rhs) const {
    permutation q;
    q.init(n);
    for(int i = 1; i <= n; i++) {
      q.p[i] = p[rhs.p[i]];
    }
    return q;
  }
}a[6], t;

permutation operator ^ (permutation p, int y) {
  permutation q;
  q.init(p.n);
  for(; y; y >>= 1, p = p * p) {
    if(y & 1) {
      q = q * p;
    }
  }
  return q;
}

