vi qpow(const vi& a, int y)
{
	vi res(a.size());
	vector<bool> vis(a.size(), false);
	for (int i = 1; i < a.size(); ++i)
	{
		if (vis[i]) continue;
		vi cycle(1, i);
		for (int u = a[i]; u != i; u = a[u])
		{
			cycle.push_back(u);
			vis[u] = true;
		}
