	int r = 0;
	for (int i = 0; i < 17; i++)
		r += (x >> i) & 1;
	return r;
}
bool recv(int x) {
	if (x == 1)
		return true;
	return recv(x / 2) & (x & 1);
}
vector<int> r;
void rec(int d, int f) {
	if (f == 0)
		return;
	int x = d & -d;
	d ^= x, f ^= x;

	if (!d) {
		rec(f & -f, f);
		r.emplace_back(x);
		rec(f & -f, f);
	}
	else {
		int cnt = popcnt(d);
		int y = 0;
		for (int i = 0; i < cnt / 2; i++) {
			y |= d & -d;
			d ^= d & -d;
		}
		int z = d;

		rec(y, f);
		r.emplace_back(x);
		rec(z, f);
	}
}
