typedef pair <int, int> ii;

struct pos {
    vector <int> seq;
    pos(int n = 0) {
        seq.resize(n + 1);
        for (int i = 1; i <= n; i++)
            seq[i] = i;
    }
};

pos Inverse(const pos &a)
{
    pos res(a.seq.size() - 1);
    for (int i = 1; i < a.seq.size(); i++)
        res.seq[a.seq[i]] = i;
    return res;
}

pos Multiply(const pos &a, const pos &b)
{
    pos c(b.seq.size() - 1);
    for (int i = 1; i < b.seq.size(); i++)
        c.seq[i] = a.seq[b.seq[i]];
    return c;
}

int main()
{
    int n, k; scanf("%d %d", &n, &k);
    pos p(n), q(n);
    for (int i = 1; i <= n; i++)
        scanf("%d", &p.seq[i]);
    for (int i = 1; i <= n; i++)
        scanf("%d", &q.seq[i]);
    pos res;
    if (k == 1) res = p;
    else if (k == 2) res = q;
    else if (k == 3) res = Multiply(q, Inverse(p));
    else {
        pos beg(n), mid(n);
        if (k % 3 == 2) {
            pos A = Multiply(q, Inverse(p));
            pos B = Multiply(Inverse(q), p);
            pos C = Multiply(A, B);
            int tims = (k - 2) / 6;
            while (tims) {
                if (tims & 1) beg = Multiply(beg, C);
                tims >>= 1; C = Multiply(C, C);
            }
            if (k % 6 == 5) beg = Multiply(beg, A);
        } else {
            pos A = Multiply(Inverse(p), Inverse(q));
            pos B = Multiply(p, q);
            pos C = Multiply(A, B);
            beg = q;
            int tims = (k - 4) / 6;
            while (tims) {
                if (tims & 1) beg = Multiply(beg, C);
                tims >>= 1; C = Multiply(C, C);
            }
            if (k % 6 == 3 || k % 6 == 1) beg = Multiply(beg, A);
        }
        if (k % 6 == 0)
            mid = Multiply(Multiply(Inverse(p), Inverse(q)), Multiply(p, p));
        else if (k % 6 == 1)
            mid = p;
        else if (k % 6 == 2)
            mid = q;
        else if (k % 6 == 3)
            mid = Multiply(Multiply(p, q), Multiply(Inverse(p), Inverse(p)));
        else if (k % 6 == 4)
            mid = Inverse(p);
        else if (k % 6 == 5)
            mid = Inverse(q);
        res = Multiply(Multiply(beg, mid), Inverse(beg));
    }
    for (int i = 1; i < res.seq.size(); i++)
        printf("%d%c", res.seq[i], i + 1 < res.seq.size()? ' ': '\n');
    return 0;
}

