vector<int> Z(string s)
{
int n = s.length();
vector<int> z(n);
int L = 0, R = 0;
for (int i = 1; i < n; i++) {
  if (i > R) {
    L = R = i;
    while (R < n && s[R-L] == s[R]) R++;
    z[i] = R-L; R--;
  } else {
    int k = i-L;
    if (z[k] < R-i+1) z[i] = z[k];
    else {
      L = i;
      while (R < n && s[R-L] == s[R]) R++;
      z[i] = R-L; R--;
    }
  }
}
return z;
}

vector<int> opvec(vector<int> a, vector<int> b)
{
    int n = a.size();
    vector<int> res(n);
    for (int i = 0; i<n; i++) res[a[i]] = b[i];
    return res;
}

vector<int> invvec(vector<int> a)
{
    int n = a.size();
    vector<int> res(n);
    for (int i = 0; i<n; i++) res[a[i]] = i;
    return res;
}

vector<int> mulvec(vector<int> a, vector<int> b)
{
    a = invvec(a);
    int n = a.size();
    vector<int> res(n);
    for (int i = 0; i<n; i++) res[a[i]] = b[i];
    return res;
}

vector<int> id(int n)
{
    vector<int> res(n);
    for (int i = 0; i<n; i++) res[i] = i;
    return res;
}

vector<int> power(vector<int> a, int deg)
{
    if (deg==0) return id(a.size());
    if (deg%2==1) return mulvec(power(a, deg-1), a);
    auto kek = power(a, deg/2);
    return mulvec(kek, kek);
}



