// r_k
vector<int> calc(long long k) {
    if (k == 0) return idv;
    if (k == 1) return q;
    --k;

    int num = k / 4;
    int r = k % 4;
    vector<int> res = idv;
    if (r == 0) res = idv;
    else if (r == 1) res = pi;
    else if (r == 2) res = pi + qi;
    else if (r == 3) res = pi + qi + p;

    auto totyu = idv;
    for (int i = 0; i < 35; ++i) {
        if (num & (1LL<<i)) {
            totyu = totyu + beki[i];
        }
    }
    res = totyu + res;
    res = q + res;
    return res;
}

vector<int> solve(long long k) {
    if (k == 1) return p;
    if (k == 2) return q;
    if (k == 3) return q + pi;
    long long a, b;
    long long d = k / 3;
    if (k % 3 == 0) a = d*2, b = d*2-2;
    else if (k % 3 == 1) a = d*2+1, b = d*2-2;
    else a = d*2+2, b = d*2-1;

    /*
    COUT("---------");
    COUT(k);
    COUT(a);
    COUT(b);
    COUT(calc(a));
    COUT(calc(b));
    */

    auto res = calc(a) + inv(calc(b));
    return res;
}

